CREATE TABLE `idigroup`.`KY_KE_TOAN` ( `MA_KKT` INT NOT NULL AUTO_INCREMENT , `TEN_KKT` VARCHAR(255) NOT NULL , `BAT_DAU` DATE NOT NULL , `KET_THUC` DATE NOT NULL , `TRANG_THAI` TINYINT NOT NULL , `MAC_DINH` TINYINT NOT NULL, PRIMARY KEY (`MA_KKT`)) ENGINE = InnoDB;

CREATE TABLE `idigroup`.`KY_KE_TOAN_SO_DU` (`MA_KKT` INT NOT NULL , `MA_TK` VARCHAR(10) NULL , `MA_DT` INT NULL, `NO_DAU_KY` DOUBLE NULL, `CO_DAU_KY` DOUBLE NULL, `NO_CUOI_KY` DOUBLE NULL, `CO_CUOI_KY` DOUBLE NULL, PRIMARY KEY (`MA_KKT`, `MA_TK`)) ENGINE = InnoDB;

ALTER TABLE `tai_khoan_danh_muc` ADD `MA_TK_NH` INT NULL AFTER `SO_DU`;
ALTER TABLE `chung_tu` ADD `MA_KHO` INT NULL AFTER `NGUOI_NOP`;

CREATE TABLE `idigroup`.`TAI_KHOAN` ( `MA_TKKT` INT NOT NULL AUTO_INCREMENT , `MA_TK` VARCHAR(10) NOT NULL ,  `SO_TIEN` double NOT NULL, `SO_DU` TINYINT(1) NOT NULL DEFAULT '-1', `LY_DO` VARCHAR(255) NULL, `ASSET_CODE` VARCHAR(10) NULL, PRIMARY KEY (`MA_TKKT`) ) ENGINE = InnoDB CHARACTER SET utf8 COLLATE utf8_general_ci;
CREATE TABLE `idigroup`.`CHUNG_TU_HANG_HOA` ( `MA_CT` INT NOT NULL , `MA_HH` INT NULL , `SO_LUONG_CT` DOUBLE NULL, `SO_LUONG_TN` DOUBLE NULL, `DON_GIA` DOUBLE NULL, PRIMARY KEY( `MA_CT`, `MA_HH`)) ENGINE = InnoDB;
CREATE TABLE `idigroup`.`CHUNG_TU_NVKT` ( `MA_CT` INT NOT NULL , `MA_HH` INT NULL , `MA_TKKT` INT NOT NULL) ENGINE = InnoDB;

-- Bảng dự phòng
CREATE TABLE `idigroup`.`CHUNG_TU_DINH_KHOAN` ( `MA_CT` INT NOT NULL , `MA_HH` INT NOT NULL DEFAULT '0', `MA_TK` VARCHAR(10) NOT NULL ,  `SO_TIEN` double NOT NULL, `SO_DU` TINYINT(1) NOT NULL DEFAULT '-1', `LY_DO` VARCHAR(255) NULL, `ASSET_CODE` VARCHAR(10) NULL, PRIMARY KEY (`MA_CT`, `MA_HH`, `MA_TK`, `SO_DU`) ) ENGINE = InnoDB CHARACTER SET utf8 COLLATE utf8_general_ci;